// Helper functions
export function getCategories(): string[] {
  const categories = new Set<string>();
  catalog.forEach(item => categories.add(item.category));
  return Array.from(categories).sort();
}

export function getSubcategories(category: string): string[] {
  const subcategories = new Set<string>();
  catalog
    .filter(item => item.category === category)
    .forEach(item => subcategories.add(item.subcategory));
  return Array.from(subcategories).sort();
}

export function getItemsByCategory(category: string): CatalogItem[] {
  return catalog.filter(item => item.category === category);
}

export function getItemsBySubcategory(category: string, subcategory: string): CatalogItem[] {
  return catalog.filter(
    item => item.category === category && item.subcategory === subcategory
  );
}

export function searchItems(query: string): CatalogItem[] {
  const lowerQuery = query.toLowerCase();
  return catalog.filter(
    item =>
      item.name.toLowerCase().includes(lowerQuery) ||
      item.category.toLowerCase().includes(lowerQuery) ||
      item.subcategory.toLowerCase().includes(lowerQuery)
  );
}
